window.addEventListener("DOMContentLoaded", () => {
  const bts = document.querySelector(".bts");
  const close = document.querySelector(".close");
  close.addEventListener("click", () => {
    const commentsActive = document.querySelectorAll(".comment.active");
    const arr = Array.from(commentsActive);
    arr.slice(`${arr.length - 5}`, `${arr.length}`).forEach((i) => {
      i.classList.remove("active");
      i.classList.add("lost");
    });
  });
  bts.addEventListener("click", () => {
    const commentsLost = document.querySelectorAll(".comment.lost");
    const arr = Array.from(commentsLost);
    arr.slice(0, 5).forEach((i) => {
      i.classList.remove("lost");
      i.classList.add("active");
    });
  });
});
