window.addEventListener("DOMContentLoaded", () => {
  const e = document.querySelector(".bts");
  document.querySelector(".close").addEventListener("click", () => {
    const e = document.querySelectorAll(".comment.active"),
      t = Array.from(e);
    t.slice("" + (t.length - 5), `${t.length}`).forEach((e) => {
      e.classList.remove("active"), e.classList.add("lost");
    });
  }),
    e.addEventListener("click", () => {
      const e = document.querySelectorAll(".comment.lost");
      Array.from(e)
        .slice(0, 5)
        .forEach((e) => {
          e.classList.remove("lost"), e.classList.add("active");
        });
    });
});
